export default class SceneInterface {
    constructor(scene) {
        this.scene = scene;
        this.time = scene.time;
        this.tweens = scene.tweens;
        this.mainContainer = scene.mainContainer;
        this.add = scene.add;
        this.emitter = scene.emitter;
        this.sort = scene.sort;
        this.anims = scene.anims;
    }

    sort() {
        this.scene.sort();
    }

    destroy() {}

    nextScene() {
        this.emitter.emit('nextScene')
    }
}
